############################################################ # Header ############################################################ TEXT(beginproblem());
############################################################ # PG Setup Code ############################################################ # Part (a) set-up
$diff = 5; $t0 = 0; $t1 = $t0 + $diff; $t2 = $t1 + $diff; $t3 = $t2 + $diff; $t4 = $t3 + $diff;
$growthRate = -15; $C0 = 285; $C1 = $C0 + $diff*$growthRate; $C2 = $C1 + $diff*$growthRate; $C3 = $C2 + $diff*$growthRate; $C4 = $C3 + $diff*$growthRate;
