Context('Numeric');
  $refreshCachedImages = 1;
$xfunc[0] = sub { my $t = shift();
  return 4/3*cos($t); };
$yfunc[0] = sub { my $t = shift();
  return sqrt(2)*sin($t); };
$xfunc[1] = sub { my $t = shift();
  return sqrt(2)*cos($t); };
$yfunc[1] = sub { my $t = shift();
  return 4/3*sin($t); };
$xfunc[2] = sub { my $t = shift();
  return 3*cos($t); };
$yfunc[2] = sub { my $t = shift();
  return sqrt(8)*sin($t); };
$xfunc[3] = sub { my $t = shift();
  return 3/4*cos($t); };
$yfunc[3] = sub { my $t = shift();
  return sqrt(1/2)*sin($t); };

 foreach my $i (0..3) {
  $gr[$i] = init_graph(-3,-3,3,3, axes=>[0,0], grid=>[6,6], size=>[240,240]);
    $fn[$i] = new Fun( $xfunc[$i], $yfunc[$i], $gr[$i] );
    $fn[$i]->domain(0,6.2832);
  }

 foreach my $i (0..3) {
 $gr[$i] -> lb('reset');
  $gr[$i] -> lb(new Label (-0.2,2,2,'black','center','middle'));
  $gr[$i] -> lb(new Label (-0.2,-2,-2,'black','center','middle'));
  $gr[$i] -> lb(new Label (2,-0.2,2,'black','center','middle'));
  $gr[$i] -> lb(new Label (-2,-0.2,-2,'black','center','middle'));
  }
  $gr[0] -> lb(new Label (-2.5,2.5,"(A)",'black','center','middle'));
