Context("Point")->flags->set( reduceConstants => 0, reduceConstantFunctions => 0,   );
$x1 = Formula("(sqrt(5),0)");
$x2 = Formula("(-sqrt(5),0)");
$xintercepts=Formula("(sqrt(5),0),(-sqrt(5),0)");
$my_evaluator = $xintercepts -> cmp(
 showHints=>1,
 showLengthHints => 1,
 partialCredit => 1,
); 
$y = Formula("(0,-5)");
$yintercepts=List("(0,-5)");
$my_evaluator2 = $yintercepts -> cmp(
 showHints=>1,
 showLengthHints => 1,
 partialCredit => 1,
);

