    $y1 = 64.6;
    $x2 = 11;
    $y2 = 28.7;
    $m = ($y2-$y1)/($x2-$x1);
    $absm = abs($m);
    $b = $y1 + $m*(0 - $x1);
    $func = Formula("$b + $m*t")->reduce;
