authored.pg-code:
Context("Fraction");
$p = Compute("-3/2");
$p2 = Compute("9/4");
Context()->flags->set( reduceConstants => 0, reduceConstantFunctions => 0,   );
$lhs = Formula("x**2-3x");
$rhs = Compute("1");
$lhs2 = Formula("x**2-3x+9/4");
$rhs2 = Compute("1+$p2");
$lin = Formula("x+$p")->reduce;
$rhs3 = Compute("13/4");
$r1 = Compute("3/2 + sqrt(13/4)");
$r2 = Compute("3/2 - sqrt(13/4)");
$answer = Formula("3/2 + sqrt(13/4), 3/2 - sqrt(13/4)");
$my_evaluator = $answer -> cmp(
 showHints=>1,
 showLengthHints => 1,
 partialCredit => 1,
); 
$answer2 = List("$r1, $r2");
$my_evaluator2 = $answer2 -> cmp(
 showHints=>1,
 showLengthHints => 1,
 partialCredit => 1,
  tolType => 'absolute',
  tolerance => .0051,
);

