showHints=>1,
showLengthHints => 1,
partialCredit => 1,
); 
$covertices = List("(5,-3+sqrt(8)), (5,-3-sqrt(8))");
$my_evaluator2 = $covertices -> cmp(
showHints=>1,
showLengthHints => 1,
partialCredit => 1,
); 
