BEGIN_PGML_SOLUTION a. [@DataTable( [ [[PGML('[`x`]'), midrule => '1', rowcss => 'border-bottom: 1px solid; ', cellcss => 'border-top: 1px solid; border-left: 1px solid; border-right: 1px solid; ',], [PGML('[`1`]'), cellcss => 'border-top: 1px solid; border-right: 1px solid; ',], [PGML('[`0`]'), cellcss => 'border-top: 1px solid; border-right: 1px solid; ',], [PGML('[`-2`]'), cellcss => 'border-top: 1px solid; border-right: 1px solid; ',], ], [[PGML('[`y`]'), rowcss => 'border-bottom: 1px solid; ', cellcss => 'border-left: 1px solid; border-right: 1px solid; ',], [PGML('[`\alert{5} `]'), cellcss => 'border-right: 1px solid; ',], [PGML('[`\alert{0}`]'), cellcss => 'border-right: 1px solid; ',], [PGML('[`\alert{-3}`]'), cellcss => 'border-right: 1px solid; ',], ], ], align => '|c|c|c|c|', columnscss => [], center => 0, );@]*
