 $f[3] = Formula("(x-4)/2");
 $xmin=-10;
 $xmax=10;
 $ymin=-10;
 $ymax=10;
 foreach my $i (0..3) {
   $yleft[$i] = $f[$i]->eval(x=>$xmin);
   $yright[$i] = $f[$i]->eval(x=>$xmax);
   $gr[$i] = init_graph($xmin,$ymin,$xmax,$ymax, axes=>[0,0], grid=>[20,20], size=>[240,240]);
 $gr[$i]->new_color("lightred",  245,234,229); 
