 $light = "lightred";
 $dark = "darkred";
 $gr[$i]->moveTo($xmin,$yleft[$i]);
 $gr[$i]->lineTo($xmax,$yright[$i],'red',3,'dashed');
 $y=0.9*$ymax*(-1)**$i;
 add_functions($gr[$i],
 "$f[$i]  for x in <$xmin,$xmax> using color:$light and weight:1");
 $gr[$i]->fillRegion([0,$y,$light]
 );
 }

foreach my $i (0..3) {
$gr[$i] -> lb('reset');
 $gr[$i] -> lb(new Label (-0.8,5,5,'black','center','middle'));
