$refreshCachedImages = 1;
$h = -4;
$k = 0;
$a = sqrt(12);
$b = 3;
$xfunc[0] = sub { my $t = shift();
return $h+$a*cosh($t); };
$yfunc[0] = sub { my $t = shift();
return $k+$b*sinh($t); };
$xfunc[1] = sub { my $t = shift();
return $h+$b*cosh($t); };
$yfunc[1] = sub { my $t = shift();
return $k+$a*sinh($t); };
$xfunc[2] = sub { my $t = shift();
return $h+$a*sinh($t); };
$yfunc[2] = sub { my $t = shift();
return $k +$b*cosh($t); };
$xfunc[3] = sub { my $t = shift();
return $h + $b*sinh($t); };
$yfunc[3] = sub { my $t = shift();
return $k+$a*cosh($t); };
$xfunc[4] = sub { my $t = shift();
return $h-$a*cosh($t); };
$yfunc[4] = sub { my $t = shift();
return $k+$b*sinh($t); };
$xfunc[5] = sub { my $t = shift();
return $h-$b*cosh($t); };
$yfunc[5] = sub { my $t = shift();
return $k+$a*sinh($t); };
$xfunc[6] = sub { my $t = shift();
return $h+$a*sinh($t); };
$yfunc[6] = sub { my $t = shift();
return $k -$b*cosh($t); };
$xfunc[7] = sub { my $t = shift();
return $h + $b*sinh($t); };
$yfunc[7] = sub { my $t = shift();
return $k-$a*cosh($t); };

foreach my $i (0..3) {
$gr[$i] = init_graph(-15,-10,5,10, axes=>[0,0], grid=>[20,20], size=>[240,240]);
$fn[$i] = new Fun( $xfunc[$i], $yfunc[$i], $gr[$i] );
$fn[$i]->domain(-3,3);
$fn[$i+4] = new Fun( $xfunc[$i+4], $yfunc[$i+4], $gr[$i] );
$fn[$i+4]->domain(-3,3);
}

foreach my $i (0..3) {
$gr[$i] -> lb('reset');
$gr[$i] -> lb(new Label (-0.8,5,5,'black','center','middle'));
$gr[$i] -> lb(new Label (-0.8,-5,-5,'black','center','middle'));
$gr[$i] -> lb(new Label (5,-0.8,5,'black','center','middle'));
$gr[$i] -> lb(new Label (-5,-0.8,-5,'black','center','middle'));
}
$gr[0] -> lb(new Label (-8.5,9,"(A)",'black','center','middle'));
$gr[1] -> lb(new Label (-8.5,9,"(B)",'black','center','middle'));
$gr[2] -> lb(new Label (-8.5,9,"(C)",'black','center','middle'));
$gr[3] -> lb(new Label (-8.5,9,"(D)",'black','center','middle'));

$mc = RadioButtons(
        [ "(A)", "(B)", "(C)","(D)"],
        2
    );
Context(Point)->flags->set( reduceConstants => 0, reduceConstantFunctions => 0,  );
$vertices = List("(5+sqrt(15),-3), (5-sqrt(15),-3)");
$my_evaluator = $vertices -> cmp(
showHints=>1,
showLengthHints => 1,
partialCredit => 1,
); 
$covertices = List("(5,-3+sqrt(8)), (5,-3-sqrt(8))");
$my_evaluator2 = $covertices -> cmp(
showHints=>1,
showLengthHints => 1,
partialCredit => 1,
); 
$l1 = Formula("sqrt(3)/2 *(x+4)");
$l2 = Formula("-sqrt(3)/2 *(x+4)");
$multians = MultiAnswer($l1,$l2)->with(
  singleResult => 0,
  checkTypes => 0,
  checker => sub {
      my ( $correct, $student, $self ) = @_;
      my ( $f1stu, $f2stu ) = @{$student};
      my ( $f1, $f2 ) = @{$correct};
      if ( ($f1 == $f1stu && $f2 == $f2stu) ||
           ($f1 == $f2stu && $f2 == $f1stu)  
      ) {
      return[1,1];
      } elsif ( ($f1 == $f1stu )  ||
               ($f2 == $f1stu )  
          ) {
          return [1,0];
      } elsif ( ($f1 == $f2stu)   ||
               ($f2 == $f2stu )  
          ) {
          return [0,1];
    } else {
        return [0,0];
    }
    }
);

